import { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";
import Home from "../layout"; // Pastikan untuk mengimpor komponen Home

function ProtectedRoute() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Mengambil data pengguna dari json-server
    fetch("http://localhost:3001/users")
      .then((response) => response.json())
      .then((data) => {
        // Cek apakah ada pengguna yang terautentikasi
        const user = data.find((user) => user.isAuthenticated); // Misalkan ada properti isAuthenticated
        setIsAuthenticated(!!user); // Set true jika ada pengguna terautentikasi
      })
      .catch((error) => console.error("Error fetching users:", error))
      .finally(() => setLoading(false)); // Set loading false setelah fetch selesai
  }, []);

  // Tampilkan loading jika data masih diambil
  if (loading) {
    return <div>Loading...</div>;
  }

  if (isAuthenticated) {
    // Jika ter-autentikasi, render komponen Home
    return <Home />;
  }

  // Jika tidak ter-autentikasi, arahkan ke halaman Sign In
  return <Navigate to="/signin" replace />;
}

export default ProtectedRoute;
