import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";

function ProtectedRoute({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(true);


 useEffect(() => {
  const checkAuthentication = async () => {
    try {
      const response = await fetch("http://localhost:3001/users"); // Fetch users from server
      if (response.ok) {
        const data = await response.json();
        // Misalkan kita ingin memeriksa apakah ada pengguna dengan ID tertentu
        const userIdToCheck = "someUserId"; // Ganti dengan ID pengguna yang ingin Anda periksa
        const user = data.find((user) => user.id === userIdToCheck); // Check if user exists
        setIsAuthenticated(!!user); // Set authentication status
      } else {
        setIsAuthenticated(false); // Server error
      }
    } catch (error) {
      setIsAuthenticated(false); // Server error
    }
  };

  checkAuthentication();
}, []);

  // Jika status autentikasi belum ditentukan, tampilkan loading atau komponen lain
  if (isAuthenticated === null) {
    return <div>Loading...</div>;
  }

  // Jika pengguna terautentikasi, render children
  if (isAuthenticated) {
    return children;
  }

  // Jika pengguna tidak terautentikasi, arahkan ke halaman SignIn
  return <Navigate to="/signin" />;
}

export default ProtectedRoute;
