import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";

function ProtectedRoute({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(null);

  useEffect(() => {
    const checkAuthentication = async () => {
      // Ambil userId dari localStorage
      const userId = localStorage.getItem("userId");
      if (!userId) {
        setIsAuthenticated(false);
        return;
      }

      try {
        // Kirim permintaan ke server untuk memeriksa autentikasi
        // const response = await fetch("http://localhost:3001/users", {
        //   method: "GET",
        // });
        const response = await fetch("http://localhost:3001/users");        
        // // Periksa apakah respons berhasil
        if (response.ok) {
          const data = await response.json();
          // Cari pengguna dengan id yang sesuai
          const user = data.find((user) => user.id === userId);
          // Jika pengguna ditemukan, pengguna terautentikasi
          if (user) {
            setIsAuthenticated(true);
          } else {
            setIsAuthenticated(false);
          }
        } else {
          setIsAuthenticated(false);
        }
      } catch (error) {
        // Jika terjadi kesalahan, pengguna tidak terautentikasi
        setIsAuthenticated(false);
      }
    };

    checkAuthentication();
  }, []);

  // Jika status autentikasi belum ditentukan, tampilkan loading atau komponen lain
  if (isAuthenticated === null) {
    return (
    <div>Loading...</div>
  );
  }

  // Jika pengguna terautentikasi, render children
  if (isAuthenticated) {
    return children;
  }

  // Jika pengguna tidak terautentikasi, arahkan ke halaman SignIn
  return <Navigate to="/signin" />;
}

export default ProtectedRoute;
