import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";

function ProtectedRoute({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(null);


  // Check authentication status on component mount
  useEffect(() => {
   const checkAuthentication = async () => {
     const userId = localStorage.getItem("userId"); // Get userId from localStorage
     if (!userId) {
       setIsAuthenticated(false); // Not authenticated
       return;
     }

     try {
       const response = await fetch("http://localhost:3001/users"); // Fetch users from server

       // Check if response is ok
       if (!response.ok) {
         throw new Error(`HTTP error! status: ${response.status}`);
       }

       const data = await response.json();
       const user = data.find((user) => user.id === userId); // Check if user exists
       setIsAuthenticated(!!user); // Set authentication status
     } catch (error) {
       setIsAuthenticated(false); // Error occurred
       console.error("Error fetching users:", error);
     }
   };
    checkAuthentication();
  }, []);

  // Jika status autentikasi belum ditentukan, tampilkan loading atau komponen lain
  if (isAuthenticated === null) {
    return <div>Loading...</div>;
  }

  // Jika pengguna terautentikasi, render children
  if (isAuthenticated) {
    return children;
  }

  // Jika pengguna tidak terautentikasi, arahkan ke halaman SignIn
  return <Navigate to="/signin" />;
}

export default ProtectedRoute;
