import { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";
import Home from "../layout/Home"; // Pastikan untuk mengimpor komponen Home

function ProtectedRoute({ children }) {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Cek apakah ada data user di localStorage
    const userId = localStorage.getItem("users");
    console.log("User ID from localStorage:", userId); // Tambahkan log ini
    if (userId) {
      setIsAuthenticated(true);
    }
    setLoading(false);
  }, []);

  // Tampilkan loading jika data masih diambil
  if (loading) {
    return <div>Loading...</div>;
  }

  if (isAuthenticated) {
    console.log("User is authenticated, rendering children."); // Tambahkan log ini
    // Jika ter-autentikasi, render children
    return children;
  }

  // Jika tidak ter-autentikasi, arahkan ke halaman Sign In
  return <Navigate to="/signin" replace />;
}

export default ProtectedRoute;
